mapper.insert:
INSERT INTO yy_yzm (
    id
    , openid
    , sjh
    , yzm
    , cjsj
    )
    VALUES
        (
        #{id}
        , #{openid}
        , #{sjh}
        , #{yzm}
        , #{cjsj}
        );

UPDATE yy_yzm SET
    sjh =  #{sjh},
    yzm = #{yzm},
    cjsj = #{cjsj}
WHERE
    openid = #{openid};
